import groovy.json.JsonSlurper
import java.utils.*
def response=messageExchange.responseHeaders
log.info response
def status=response["#status#"]
log.info status
def expected=["HTTP/1.1 200 OK"]
assert status==expected
if(status==expected){
	log.info "passed"
}
else{
	log.info "not passed"
}
def respo=context.testCase.getTestStepByName('Getcategorybyid_modify').getPropertyValue("Response")
log.info respo
def holder=new JsonSlurper().parseText(respo)
String name1= holder.data.name
String title=holder.data.meta_title
log.info title
log.info name1
String name=context.expand('${#Project#name}')
log.info name
assert name==name1
if(name==name1){
	log.info "name is matched"
}
else{
	log.info "name is not matched"
}


String exp="[meta-title]"
assert title==exp
if(title==exp){
	log.info "title is matched"
}
else{
	log.info "title is not matched"
}

